import React from 'react'
import ReactDOM from 'react-dom'
import 'app.css'

class App extends Component {
    constructor(props){
        super(props)
        this.state = {
            copo: 'agua',
            clock: 1000,
        }
    }

    alteraCopo = () =>{
        this.setState = {
            copo: 'refrigerante'
        }
    }


    render(){
        return(
            <div>
                <h1>{clock}</h1>
                <button onClick={alteraCopo()}>{copo}</button>
            </div>
        )
    }




}


const rootElement = document.getElementById('root');
ReactDOM.render(<App/>, rootElement);
